// src/components/UserPage.js
import React, { useEffect, useState } from 'react';
import { listFiles, uploadFile } from '../api'; // Your API functions
import FileUpload from './FileManagement/FileUpload';
import FileList from './FileManagement/FileList';

const UserPage = () => {
    const [files, setFiles] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setAuthToken(token);
            const fetchFiles = async () => {
                const filesData = await listFiles(); // Fetch user's files
                setFiles(filesData);
            };
            fetchFiles();
        }
    }, []);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setAuthToken(token);
        }
    }, []);

    return (
        <div>
            <h1>Your Files</h1>
            <FileUpload onUploadSuccess={() => fetchFiles()} /> {/* Refresh files after upload */}
            <FileList files={files} />
        </div>
    );
};

export default UserPage;
